public String getNombre () {
    return bNombre.getText();
}

public int getHoras () {
    int s=0;
    if(!bHoras.getText().equals("")){
        return Integer.parseInt(bHoras.getText());
    }else{
        return s;
    }
}

public E2 getTitulacion () {

        E2 s= E2.SIN_ESTUDIOS;
        if (jDoctorado.isSelected()) {s = E2.DOCTORADO;}
        else if (jMaster.isSelected()) { s= E2.MASTER;}
        else if (jGrado.isSelected()) { s= E2.GRADO;}
        else if (jFP.isSelected()) { s= E2.FP;}
        else if (jBachillerato.isSelected()) { s = E2.BACHILLERATO;}
        else if (jSecundaria.isSelected()) { s= E2.SECUNDARIA;}
   
        return s;
}

public String getLugar () {
    return bLugar.getText();
}

public Date getInicio () throws ParseException {
    SimpleDateFormat formatoDelTexto = new SimpleDateFormat("dd/MM/yyyy");
    Date f= formatoDelTexto.parse(bInicio.getText());
    return f;
}


